        public Web3 GetWeb3Instance(string senderAddress)
        {
            return new Web3(new ManagedAccount(senderAddress, "password"), "HTTP://127.0.0.1:7545");
        }

        public async Task<TransactionReceipt> CreateNewPatientJourneyRequestContract(string senderAddress)
        {
            var web3 = GetWeb3Instance(senderAddress);
            var transactionHash = await web3.Eth.DeployContract.SendRequestAsync(_abi, _byteCode, senderAddress, new HexBigInteger(999999));
            var receipt = await web3.Eth.Transactions.GetTransactionReceipt.SendRequestAsync(transactionHash);

            while (receipt == null)
            {
                Thread.Sleep(5000);
                receipt = await web3.Eth.Transactions.GetTransactionReceipt.SendRequestAsync(transactionHash);
            }

            return receipt;
        }


        public Contract GetContract(Web3 web3, string contractAddress)
        {
            _contract = web3.Eth.GetContract(_abi, contractAddress);
            return _contract;
        }
